'use strict';

const { kansuujiPattern } = require('jpdate-lib');

const week = ['日', '月', '火', '水', '木', '金', '土'];

const dayPattern = `((第|だい)(いち|に|さん|よん|ご|${kansuujiPattern}|[0-9０-９]+))?(日|月|火|水|木|金|土)曜日?`;
const dayRegExp = new RegExp(dayPattern);
exports.dayRegExp = dayRegExp;

// 文字列から曜日の取得
const getDayFromStr = (str) => {
  const match = str.match(dayRegExp);
  const matchDay = week.indexOf(match[4]);
  return matchDay;
};
exports.getDayFromStr = getDayFromStr;
